mapper.select:
select * from custom_list_value

  and uuid=#{uuid}
  and custom_list_uuid=#{customListUuid}
  and data_value=#{dataValue}
  and column_values=#{columnValues}
  and partner_code=#{partnerCode}

order by id DESC

mapper.insert:
        update custom_list_value set
          gmt_modify=#{gmtModify},
	      operator=#{operator},
		  gmt_expire=#{gmtExpire}
		where uuid = #{uuid}
